INSERT INTO communes (communesid, commune, cp, description, lat, lng) VALUES ("2","Bapaume","62450","<p>Besoin des services d&#39;un diagnostiqueur immobilier certifi&eacute; pour r&eacute;aliser votre projet immobilier dans le Pas-de-Calais, dans la commune de Bapaume ? Quelles que soient vos attentes &agrave; ce propos, &agrave; savoir que vous ayez besoin des services d&#39;un professionnel pour r&eacute;aliser votre diagnostic de performance &eacute;nerg&eacute;tique (DPE), votre diagnostic amiante, votre diagnostic plomb ou encore votre diagnostic &eacute;lectricit&eacute;, nous poss&eacute;dons l&#39;expertise requise pour vous accompagner.</p>\r\n<p>L&#39;expertise mais &eacute;galement la certification, comme le pr&eacute;voit la l&eacute;gislation.</p>\r\n<p>Vous pouvez par cons&eacute;quent nous solliciter en toute confiance, nous ferons le n&eacute;cessaire pour &eacute;tablir et vous d&eacute;livrer votre DPE &agrave; Bapaume dans les meilleurs d&eacute;lais, et dans le respect des exigences r&eacute;glementaires.</p>\r\n<p>Alors n&#39;h&eacute;sitez pas et appelez-nous !&nbsp;\r\n\r\n	&nbsp;</p>","50.101390838623004","2.8511109352111994");
