INSERT INTO communes (communesid, commune, cp, description, lat, lng) VALUES ("51","Tourcoing","59200","<p>Avant que vous ne remettiez &agrave; son nouveau locataire les cl&eacute;s de votre maison de Tourcoing, pensez &agrave; faire le n&eacute;cessaire concernant l&#39;&eacute;tat des lieux d&#39;entr&eacute;e.</p>\r\n<p>Ce document doit en effet &ecirc;tre int&eacute;gr&eacute; au contrat de location au moment de l&#39;entr&eacute;e dans les lieux.</p>\r\n<p>Et n&#39;oubliez pas qu&#39;un &eacute;tat des lieux exhaustif et complet constitue une garantie pour le propri&eacute;taire bailleur, comme d&#39;ailleurs pour le locataire.</p>\r\n<p>Alors si vous d&eacute;sirez une relation sereine avec votre locataire, nous vous proposons nos services afin de r&eacute;aliser votre &eacute;tat des lieux d&#39;entr&eacute;e &agrave; Tourcoing, puis l&#39;&eacute;tat des lieux de sortie &agrave; la fin du contrat.</p>\r\n<p>Contactez-nous pour cela !&nbsp;\r\n\r\n	&nbsp;</p>","50.721389770508004","3.1613891124725004");
