INSERT INTO communes (communesid, commune, cp, description, lat, lng) VALUES ("52","Tourcoing","59200","<p>Les artisans que vous avez mandat&eacute;s pour votre projet de r&eacute;novation &agrave; Tourcoing sont sur le point d&#39;intervenir ? Avez-vous pris soin de faire le point sur les &eacute;ventuels diagnostics obligatoires au pr&eacute;alable ? Sachez que vous pouvez notamment &ecirc;tre concern&eacute; par le diagnostic amiante avant travaux (DAAT).</p>\r\n<p>C&#39;est en effet le cas si votre bien immobilier a &eacute;t&eacute; construit avant le 1er juillet 1997, auquel cas vous ne pourrez lancer votre chantier sans ce diagnostic ; la d&eacute;marche visant &agrave; pr&eacute;venir les risques d&#39;exposition &agrave; l&#39;amiante.</p>\r\n<p>Alors n&#39;attendez plus et contactez-nous pour fixer un rendez-vous concernant votre diagnostic amiante avant travaux &agrave; Tourcoing !\r\n\r\n	&nbsp;</p>","50.721389770508004","3.1613891124725004");
