INSERT INTO communes (communesid, commune, cp, description, lat, lng) VALUES ("53","Hazebrouck","59190","<p>Pensez &agrave; v&eacute;rifier que le dossier de diagnostic technique concernant la maison que vous souhaitez vendre &agrave; Hazebrouck contient bien l&#39;ensemble des documents requis pour la transaction ! Selon les cas, ces diagnostics peuvent aller du diagnostic amiante au diagnostic plomb, en passant par le diagnostic termites, le diagnostic de performance &eacute;nerg&eacute;tique, l&#39;&eacute;tat des risques et pollutions des sols ou encore le diagnostic &eacute;lectricit&eacute;.</p>\r\n<p>D&#39;o&ugrave; l&#39;int&eacute;r&ecirc;t de solliciter l&#39;expertise d&#39;un professionnel, d&#39;une part pour &eacute;tablir la liste des documents &agrave; int&eacute;grer au dossier de diagnostic technique, et ensuite pour intervenir et r&eacute;aliser votre dossier de diagnostic immobilier &agrave; Hazebrouck.\r\n\r\n	&nbsp;</p>","50.721942901611001","2.5386109352111994");
